import { cx, cy, height, width, x, y } from 'svg.js/src/modules/core/circled.js'
import {
  extend,
  nodeOrNew,
  register,
  wrapWithAttrCheck
} from 'svg.js/src/utils/adopter.js'
import { registerMethods } from 'svg.js/src/utils/methods.js'
import SVGNumber from 'svg.js/src/types/SVGNumber.js'
import Shape from 'svg.js/src/elements/Shape.js'

export default class Circle extends Shape {
  constructor (node) {
    super(nodeOrNew('circle', node), node)
  }

  radius (r) {
    return this.attr('r', r)
  }

  // Radius x value
  rx (rx) {
    return this.attr('r', rx)
  }

  // Alias radius x value
  ry (ry) {
    return this.rx(ry)
  }

  size (size) {
    return this.radius(new SVGNumber(size).divide(2))
  }
}

extend(Circle, { x, y, cx, cy, width, height })

registerMethods({
  Container: {
    // Create circle element
    circle: wrapWithAttrCheck(function (size) {
      return this.put(new Circle())
        .size(size)
        .move(0, 0)
    })
  }
})

register(Circle, 'Circle')
