import { nodeOrNew, register } from 'svg.js/src/utils/adopter.js'
import Container from 'svg.js/src/elements/Container.js'

export default class Defs extends Container {
  constructor (node) {
    super(nodeOrNew('defs', node), node)
  }

  flatten () {
    return this
  }

  ungroup () {
    return this
  }
}

register(Defs, 'Defs')
