import {
  extend,
  nodeOrNew,
  register,
  wrapWithAttrCheck
} from 'svg.js/src/utils/adopter.js'
import { registerMethods } from 'svg.js/src/utils/methods.js'
import { rx, ry } from 'svg.js/src/modules/core/circled.js'
import Shape from 'svg.js/src/elements/Shape.js'

export default class Rect extends Shape {
  // Initialize node
  constructor (node) {
    super(nodeOrNew('rect', node), node)
  }
}

extend(Rect, { rx, ry })

registerMethods({
  Container: {
    // Create a rect element
    rect: wrapWithAttrCheck(function (width, height) {
      return this.put(new Rect()).size(width, height)
    })
  }
})

register(Rect, 'Rect')
